import styled from 'styled-components'

export const UserCard = styled.div`
  margin: 30px 10px 10px 10px;
  padding: 10px;
  border-radius: 10px;
  border: 1px solid gold;
`

export const CardText = styled.div`
  text-align: center;
  font-family: 'Saira', sans-serif;
  @media (min-width: 768px) {
    font-size: 24px;
  }
`
